    protected bool SaveOrEditCheck()
    {
        bool isOk = false;
        long decProductId = 0;
        long decBatchId = 0;
        decimal decCalcQty = 0;
        //StockPostingSP spStockPosting = new StockPostingSP();
        string strStatus = setting.NegativestockStatus;
        bool isNegativeLedger = false;
        DataTable dtblPurchaseMasterViewById = new DataTable();
        if (TodoList != null)
        {

            foreach (var list in TodoList)
            {
                decProductId = list.ProductId;
                if (list.BatchId > 0)
                {
                    decBatchId = list.BatchId;
                }
                decimal decCurrentStock = stockPosting.StockCheckForProductSale(decProductId, decBatchId);
                if (list.Qty > 0)
                {
                    decCalcQty = decCurrentStock - list.Qty;
                }
                if (decCalcQty < 0)
                {
                    isNegativeLedger = true;
                    break;
                }
            }
            if (isNegativeLedger)
            {
                if (strStatus == "Warn")
                {
                    Toaster.Add("Negative Stock balance exists,Do you want to Continue.", MatToastType.Info, "PurchaseReturn", null);
                    isOk = true;
                }
                else if (strStatus == "Block")
                {
                    Toaster.Add("Cannot continue ,due to negative stock balance.", MatToastType.Info, "PurchaseReturn", null);
                    isOk = false;
                }
                else
                {
                    isOk = true;
                }
            }
            else
            {
                isOk = true;
            }
        }
        else
        {
            isOk = false;
        }
        return isOk;
    }
