    byte[] readfile(string strimagepath)
    {
        System.IO.FileInfo flinfo = new System.IO.FileInfo(strimagepath);
        long numbytes = flinfo.Length;
        FileStream fstream = new FileStream(strimagepath, FileMode.Open, FileAccess.Read);
        BinaryReader br = new BinaryReader(fstream);
        ImageUploaded = br.ReadBytes((int)numbytes);
        return ImageUploaded;
    }
    async Task Submit()
    {
        if (strEdit == true)
        {
            //CheckNegativeStockAndNegativeLedger
            long decProductId = 0;
            long decBatchId = 0;
            decimal decCalcQty = 0;
            string strStatus = setting.NegativestockStatus;
            bool isNegativeLedger = false;
            DataTable dtblPurchaseMasterViewById = new DataTable();
            if (TodoList != null)
            {

                foreach (var list in TodoList)
                {
                    decProductId = list.ProductId;
                    if (list.BatchId > 0)
                    {
                        decBatchId = list.BatchId;
                    }
                    decimal decCurrentStock = stockPosting.StockCheckForProductSale(decProductId, decBatchId);
                    if (list.Qty > 0)
                    {
                        decCalcQty = decCurrentStock - list.Qty;
                    }
                    if (decCalcQty < 0)
                    {
                        isNegativeLedger = true;
                        break;
                    }
                }
                if (isNegativeLedger)
                {
                    if (strStatus == "Warn")
                    {
                        bool confirmed = await JsRuntime.InvokeAsync<bool>("confirm", "Negative Stock balance exists,Do you want to Continue?");
                        if (confirmed)
                        {
                            SaveOrEdit();

                        }
                    }
                    else if (strStatus == "Block")
                    {
                        Toaster.Add("Cannot continue ,due to negative stock balance.", MatToastType.Info, "Sales Invoice", null);
                    }
                    else
                    {
                        SaveOrEdit();
                    }
                }
                else
                {
                    SaveOrEdit();
                }
            }
        }
        else
        {
            Toaster.Add("You dont have to privilege.", MatToastType.Success, "dashboard", null);
            navManager.NavigateTo("dashboard");
        }
    }
