    public async Task HandleValidSubmit()
    {
        if (master.GroupId == 0)
        {
            Snackbar.Add("Choose Product Group.", Severity.Info);
        }
        else if (master.BrandId == 0)
        {
            Snackbar.Add("Choose Brand.", Severity.Info);
        }
        else if (master.UnitId == 0)
        {
            Snackbar.Add("Choose Unit.", Severity.Info);
        }
        else if (master.MinimumStock > master.MaximumStock)
        {
            Snackbar.Add("MinimumStock cannot be greater then minimumstock.", Severity.Info);
        }
        else
        {
           if(master.ImageUrls !=null)
           {
                        foreach (var imageUrl in master.ImageUrls)
                        {
                            master.Image = imageUrl;
                        }
           }
            master.ModifyDate = DateTime.Now;
            master.CompanyId = 1;
            var isCheck = _product.CheckNameId(master.ProductCode);
            if (isCheck == master.ProductId || isCheck == 0)
            {
                _product.Update(master);
                Snackbar.Add("Saved Successfully.", Severity.Success);
                navManager.NavigateTo("/product/productlist");
            }
            else
            {
                Snackbar.Add("Already Exists.", Severity.Info);
            }
        }
    }
    internal async Task DeletePhoto(string imageUrl)
    {
        try
        {
            var imageIndex = master.ImageUrls.FindIndex(x => x == imageUrl);
            var imageName = imageUrl.Replace($"{navManager.BaseUri}RoomImages/", "");
            if (master.ProductId == 0)
            {
                var result = FileUpload.DeleteFile(imageName);
            }
            else
            {
                //update
                DeletedImageNames ??= new List<string>();
                DeletedImageNames.Add(imageUrl);
            }
            master.ImageUrls.RemoveAt(imageIndex);
        }
        catch (Exception ex)
        {
            Snackbar.Add(ex.Message, Severity.Info);
        }

    }
    private async Task HandleImageUpload(InputFileChangeEventArgs e)
    {
        IsImageUploadProcessStarted = true;
        try
        {
            var images = new List<string>();
            if (e.GetMultipleFiles().Count > 0)
            {
                foreach (var file in e.GetMultipleFiles())
                {
                    System.IO.FileInfo fileInfo = new System.IO.FileInfo(file.Name);
                    if (fileInfo.Extension.ToLower() == ".jpg" ||
                        fileInfo.Extension.ToLower() == ".png" ||
                        fileInfo.Extension.ToLower() == ".jpeg")
                    {
                        var uploadedImagePath = await FileUpload.UploadFile(file);
                        images.Add(uploadedImagePath);
                    }
                    else
                    {
                        Snackbar.Add("Please select .jpg/.jpeg/.png file only", Severity.Info);
                        return;
                    }
                }

                if (images.Any())
                {
                    if (master.ImageUrls != null && master.ImageUrls.Any())
                    {
                        master.ImageUrls.AddRange(images);
                    }
                    else
                    {
                        master.ImageUrls = new List<string>();
                        master.ImageUrls.AddRange(images);
                    }
                }
                else
                {
                    Snackbar.Add("Image uploading failed", Severity.Info);
                    return;
                }
            }
            IsImageUploadProcessStarted = false;
        }
        catch (Exception ex)
        {
            Snackbar.Add(ex.Message, Severity.Info);
        }
    }
    void Cancel()
    {
        navManager.NavigateTo("/product/productlist");
