    private void LoadCompany()
    {
        master = _company.Edit(1); //1 is companyid

        listCurrency = _currency.GetAll();
        listWarehouse = _warehouse.GetAll();
        listVoucherype = _voucherType.GetAll();
        listCustomer = _accountLedger.ViewAllCustomer(1);
    }
    private async void SaveCompany()
    {
        if(master.CurrencyId == 0)
        {
            Snackbar.Add("Choose Default Currency.", Severity.Info);
        }
        else if(master.WarehouseId == 0)
        {
            Snackbar.Add("Choose Default Warehouse.", Severity.Info);
        }
        else
        {
            //@if (master.ImageUrls.Count > 0)
            //        {
            foreach (var imageUrl in master.ImageUrls)
            {
                if (imageUrl != null)
                {
                    master.Logo = imageUrl;
                }
                else
                {
                    master.Logo = master.Logo;
                }
            }
            master.ModifyDate = DateTime.UtcNow;
            master.CountryId = 1;
            master.FinancialYearId = 1;
            master.BusinessType = "Shop";
            _company.Update(master);
                Snackbar.Add("Updated Successfully.", Severity.Success);
                LoadCompany();
        }
    }
    private async Task HandleImageUpload(InputFileChangeEventArgs e)
    {
        try
        {
            var images = new List<string>();
            if (e.GetMultipleFiles().Count > 0)
            {
                foreach (var file in e.GetMultipleFiles())
                {
                    System.IO.FileInfo fileInfo = new System.IO.FileInfo(file.Name);
                    if (fileInfo.Extension.ToLower() == ".jpg" ||
                        fileInfo.Extension.ToLower() == ".png" ||
                        fileInfo.Extension.ToLower() == ".jpeg")
                    {
                        var uploadedImagePath = await FileUpload.UploadFile(file);
                        images.Add(uploadedImagePath);
                    }
                    else
                    {
                        Snackbar.Add("Please select .jpg/.jpeg/.png file only", Severity.Info);
                        return;
                    }
                }

                if (images.Any())
                {
                    if (master.ImageUrls != null && master.ImageUrls.Any())
                    {
                        master.ImageUrls.AddRange(images);
                    }
                    else
                    {
                        master.ImageUrls = new List<string>();
                        master.ImageUrls.AddRange(images);
                    }
                }
                else
                {
                    Snackbar.Add("Image uploading failed", Severity.Info);
                    return;
                }
            }
        }
        catch (Exception ex)
        {
            Snackbar.Add(ex.Message, Severity.Info);
        }
    }

