    AccountLedger customerType = new AccountLedger();
    List<AccountGroup> listUnder = new List<AccountGroup>();
    List<Area> listArea = new List<Area>();
    //GetSessionInfo
    long CompanyId;
    long RoleId;
    long FinancialYearId;
    long UserId;
    public byte[] ImageUploaded { get; set; }
    string ledgerNme = string.Empty;
    string ledgerNo = string.Empty;
    Privilege privi = new Privilege();
    string strMenu = "AccountLedger";
    bool strAdd = true;
    bool strEdit = true;
    bool strDelete = true;
    bool strShow = true;
    protected async override void OnInitialized()
    {
        CompanyId = await sessionStorage.GetItemAsync<long>("CompanyId");
        RoleId = await sessionStorage.GetItemAsync<long>("RoleId");
        FinancialYearId = await sessionStorage.GetItemAsync<long>("FinancialYearId");
        UserId = await sessionStorage.GetItemAsync<long>("UserId");
        //CheckPriviliage
        privi = priviliageRepository.PriviliageCheck(strMenu, RoleId, CompanyId);
        if (privi != null)
        {
            strAdd = privi.AddAction;
            strEdit = privi.EditAction;
            strDelete = privi.DeleteAction;
            strShow = privi.ShowAction;
            if (strShow == false)
            {
                Snackbar.Add("You dont have to privilege.", Severity.Error);
                navManager.NavigateTo("dashboard");
            }
        }
        else
        {
            strAdd = true;
            strEdit = true;
            strDelete = true;
            strShow = true;
        }
        LoadData();
        StateHasChanged();
    }
    protected void LoadData()
    {
        var strSerialNo = areaservices.SerialNoCode(CompanyId);
        customerType.LedgerCode = strSerialNo;
        listArea = areaRepository.ViewAllArea(CompanyId);
        customerType.AreaId = 1;
        customerType.AccountGroupId = 0;
        listUnder = accountgroup.GetUnder();
        GetDefaultImage();
    }
    public void GetDefaultImage() //This function is used to pass the default image when there is no image for saving.
    {
        // To get deafult image this function is used
        // As we store default image in start up path,  we assign the file path as its path
        string strImagePath = Path.Combine(Environment.CurrentDirectory, @"logo\\logo.png");
        ImageUploaded = readfile(strImagePath);
        MemoryStream ms = new MemoryStream(ImageUploaded);
        //Image newImage = Image.FromStream(ms);
        //pbxCompnayLogo.Image = newImage;
        //pbxCompnayLogo.SizeMode = PictureBoxSizeMode.StretchImage;

    }
    byte[] readfile(string strimagepath)
    {
        FileInfo flinfo = new FileInfo(strimagepath);
        long numbytes = flinfo.Length;
        FileStream fstream = new FileStream(strimagepath, FileMode.Open, FileAccess.Read);
        BinaryReader br = new BinaryReader(fstream);
        ImageUploaded = br.ReadBytes((int)numbytes);
        return ImageUploaded;
    }
    public void HandleValidSubmit()
    {
        if (strAdd == true)
        {
            if (ImageUploaded != null)
            {
                customerType.LogoC = ImageUploaded;
                ImageUploaded = null;
            }

            //customerType.LogoC = null;
            customerType.LogoPathC = string.Empty;
            customerType.OpeningBalance = 0;
            //customerType.AreaId = 1;
            customerType.PricinglevelId = 1;
            customerType.RouteId = 1;
            customerType.SkypeID = string.Empty;
            customerType.Twitter = string.Empty;
            customerType.Facebook = string.Empty;
            customerType.instagram = string.Empty;
            customerType.Country = string.Empty;
            customerType.State = string.Empty;
            customerType.Mnucapality = string.Empty;
            customerType.Street = string.Empty;
            customerType.ZipCode = string.Empty;
            customerType.Website = string.Empty;
            customerType.Map = string.Empty;
            customerType.CrOrDr = "Dr";
            customerType.Referfrom = "Previous Customer";
            customerType.AddedDate = DateTime.Now;
            customerType.CompanyId = CompanyId;
            customerType.CreatedBy = UserId;
            bool isSave = false;
            isSave = areaservices.InsertAccountLedger(customerType);
            if (isSave == true)
            {
                Snackbar.Add("Saved Successfully.", Severity.Error);
                LoadData();
                navManager.NavigateTo("account/accountledgerlist");
            }
            else
            {
                Snackbar.Add("Already Exists.", Severity.Error);
                navManager.NavigateTo("account/accountledgeradd");
            }
        }
        else
        {
            Snackbar.Add("You dont have to privilege.", Severity.Error);
            navManager.NavigateTo("dashboard");
        }
    }
    async Task HandleSelection(IFileListEntry[] files)
    {
        var file = files.FirstOrDefault();
        if (file != null)
        {
            var ms = new MemoryStream();
            await file.Data.CopyToAsync(ms);
            ImageUploaded = ms.ToArray();
        }
    }

    string convertImageToDisplay(byte[] image)
    {
        if (image != null)
        {
            var base64 = Convert.ToBase64String(image);
            var finalStr = string.Format("data:image/jpg;base64,{0}", base64);
            return finalStr;
        }
        return "";
    }
    void Cancel()
    {
        navManager.NavigateTo("account/accountledgerlist");
    }


