    protected async override void OnInitialized()
    {
        Email = await sessionStorage.GetItemAsync<string>("Email");
        CompanyId = await sessionStorage.GetItemAsync<long>("CompanyId");
        RoleId = await sessionStorage.GetItemAsync<long>("RoleId");
        FinancialYearId = await sessionStorage.GetItemAsync<long>("FinancialYearId");
        UserId = await sessionStorage.GetItemAsync<long>("UserId");
        LoadData();
        StateHasChanged();

    }
    protected void LoadData()
    {
        //CheckPriviliage
        privi = priviliageRepository.PriviliageCheck(strMenu, RoleId, CompanyId);
        if (privi != null)
        {
            strAdd = privi.AddAction;
            strEdit = privi.EditAction;
            strDelete = privi.DeleteAction;
            strShow = privi.ShowAction;
            if (strShow == false)
            {
                Toaster.Add("You dont have to privilege.", MatToastType.Success, "dashboard", null);
                navManager.NavigateTo("dashboard");
            }
        }
        else
        {
            strAdd = true;
            strEdit = true;
            strDelete = true;
            strShow = true;
        }
        unit = productRepository.ViewAllProductGroup();
    }
    async Task Delete(long id)
    {
        if (strDelete == true)
        {
            bool confirmed = await JsRuntime.InvokeAsync<bool>("confirm", "Are you sure?");
            if (confirmed)
            {
                if (id != 1)
                {
                    bool isSave = false;
                    isSave = productRepository.DeletegroupName(id);
                    if (isSave == true)
                    {
                        Toaster.Add("Delete Successfully.", MatToastType.Info, "ProductGroup List", null);

                        navManager.NavigateTo("productgrouplist");
                    }
                    else
                    {
                        Toaster.Add("Cannot be delete. already in used.", MatToastType.Info, "ProductGroup List", null);
                        navManager.NavigateTo("productgrouplist");
                    }
                }
                else
                {
                    Toaster.Add("Default data cannot be delete.", MatToastType.Info, "ProductGroup List", null);
