    byte[] readfile(string strimagepath)
    {
        System.IO.FileInfo flinfo = new System.IO.FileInfo(strimagepath);
        long numbytes = flinfo.Length;
        FileStream fstream = new FileStream(strimagepath, FileMode.Open, FileAccess.Read);
        BinaryReader br = new BinaryReader(fstream);
        ImageUploaded = br.ReadBytes((int)numbytes);
        return ImageUploaded;
    }
    async Task HandleSelection(IFileListEntry[] files)
    {
        var file = files.FirstOrDefault();
        if (file != null)
        {
            var ms = new MemoryStream();
            await file.Data.CopyToAsync(ms);
            ImageUploaded = ms.ToArray();
        }
    }

    string convertImageToDisplay(byte[] image)
    {
        if (image != null)
        {
            var base64 = Convert.ToBase64String(image);
            var finalStr = string.Format("data:image/jpg;base64,{0}", base64);
            return finalStr;
        }
        return "";
    }
    protected void SaveCustomerType()
    {
        if (companyview.CompanyName == string.Empty)
        {
            Snackbar.Add("Type Company Name.", Severity.Info);
            return;
        }
        else if (companyview.Address == string.Empty)
        {
            Snackbar.Add("Type Address", Severity.Info);
            return;
        }
        else if (companyview.Phone == string.Empty)
        {
            Snackbar.Add("Type Phone No.", Severity.Info);
            return;
        }
        else
        {
            if (strAdd == true)
            {
                var isCheck = companyRepository.CheckCompanyName(CompanyId);
                if (isCheck == companyview.CompanyId || isCheck == 0)
                {
                    SqlConnection sqlcon = new SqlConnection(_conn.DbConn);
                    sqlcon.Open();
                    SqlTransaction sql = sqlcon.BeginTransaction();
                    var para = new DynamicParameters();
                    if (ImageUploaded != null)
                    {
                        companyview.LogoPath = ImageUploaded;
                        ImageUploaded = null;
                    }
                    para.Add("@CompanyId", CompanyId);
                    para.Add("@CompanyName", companyview.CompanyName);
                    para.Add("@Address", companyview.Address);
                    para.Add("@Phone", companyview.Phone);
                    para.Add("@Mobile", companyview.Mobile);
                    para.Add("@EmailId", companyview.EmailId);
                    para.Add("@Pan", companyview.Pan);
                    para.Add("@Country", companyview.Country);
                    para.Add("@State", companyview.State);
                    para.Add("@Estd", companyview.Estd);
                    para.Add("@Code", companyview.Code);
                    para.Add("@NoofDecimal", companyview.NoofDecimal);
                    para.Add("@LogoPath", companyview.LogoPath);
                    sqlcon.Execute("UPDATE Company SET CompanyName=@CompanyName,Address=@Address,Phone=@Phone,Mobile=@Mobile,EmailId=@EmailId,Pan=@Pan,Country=@Country,State=@State,Estd=@Estd,Code=@Code,NoofDecimal=@NoofDecimal,LogoPath=@LogoPath where CompanyId=@CompanyId", para, sql, 0, CommandType.Text);
                    sql.Commit();
                    Snackbar.Add("Saved Successfully.", Severity.Success);
                    Load();
                }
                else
                {
                    Snackbar.Add("Already Exists.", Severity.Info);

                }
            }
            else
            {
                Snackbar.Add("You dont have to privilege.", Severity.Error);
                //Toaster.Add("You dont have to privilege.", MatToastType.Success, "Dashboard", null);
                navManager.NavigateTo("dashboard");
            }
        }
    }

